import React from 'react';
import ReactDOM from 'react-dom/client';
import { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';


 function App1() {
  const { register, handleSubmit, formState : { errors } } = useForm({ shouldUseNativeValidation: true });
  const onSubmit = async  data => { console.log(data); };
  const handleError = (errors) => {};

  const errorMsg = {
    firstName : {required : "Please enter your FirstName"}
  };

  return (
    <form onSubmit={handleSubmit(onSubmit, handleError)}>
      <input
        {...register("firstName", errorMsg.firstName)} tooltip='{errorMsg.firstName}' // custom message
      />
      <small className="text-danger">{errors?.firstName && errors.firstName.msg } </small>
      <input type="submit" />
    </form>
  );
}
const root = ReactDOM.createRoot(document.getElementById("root"));
//root.render(<EmployeeForm/>);
root.render(<App1/>);
