const EmployeeForm = () => {
    const { empEntry, handleSubmit, formState: {errors} } = useForm();
    const handleEntry = (data) => console.log(data);
    const handleError = (errors) => {};

    const errorMsg = {
        empName : { required : "Employee Name is required"},
        emailId : { required : "Email Id is required"},
        dob : { required : "Date of Birth is mandatory"},
        joinDate : { required : "Joining Date is mandatory"}
    };

    return(
       <form onSubmit={handleSubmit(handleEntry, handleError)}>
        <div>
        <label>Employee Name</label>
        <input name="empName" type="text" {...empEntry('empName', errorMsg.empName)}/>
        </div>
        <div>
                <label>Employee EmailId</label>
        <input name="emailId" type="text" {...empEntry('emailId', errorMsg.emailId)}/>
        </div>
        <div>
                <label>Employee Date Of Birth</label>
                  <input name="dob" type="date" {...empEntry('dob', errorMsg.dob)}/>
        </div>
        <div>
            <label>Employee Joining Date</label>
            <input name="joinDate" type="date" {...empEntry('joiningDate', errorMsg.joinDate)}/>
        </div>
        <button type="submit">Submit</button>
       </form>

    );
}

