import React from 'react';
import ReactDOM from 'react-dom/client';
import { useForm } from 'react-hook-form';


 const EmployeeForm = () => {
     const { register, handleSubmit, formState : {errors} } = useForm({
             shouldUseNativeValidation : true
     }
     );
     const handleEntry = (data) => {
        console.log(data);
        //data.preventDefault();
     }
     const handleError = (errors) => {
        console.log("errors caught:" + errors);
        if(errors) {
            this.setState(errors);
        }
     };

     const errorMsg = {
         empName : { required : "Employee Name is required"},
         emailId : { required : "Email Id is required"},
         dob : { required : "Date of Birth is mandatory"},
         joinDate : { required : "Joining Date is mandatory"}
     };

     return(
        <form onSubmit={handleSubmit(handleEntry, handleError)}>
         <div>
         <label>Employee Name</label><br/>
         <input {...register("empName", errorMsg.empName)} />
        <small className="text-danger">
                                  {errors?.empName && errors.empName.message}
                                </small>
         </div>
         <hr/>
         <div>
                 <label>Employee EmailId</label><br/>
         <input name="emailId" type="text" {...register("emailId",  errorM-sg.emailId)}/>
           <small className="text-danger">
                          {errors?.emailId && errors.emailId.message}
                        </small>
         </div>
         <hr/>
         <div>
                 <label>Employee Date Of Birth</label><br/>
                   <input name="dob" type="date" {...register("dob", errorMsg.dob)}/>
                <small className="text-danger">
                          {errors?.dob && errors.dob.message}
                        </small>
         </div>
         <hr/>
         <div>
             <label>Employee Joining Date</label><br/>
             <input name="joinDate" type="date" {...register("joinDate", errorMsg.joinDate)}/>
                <small className="text-danger">
                          {errors?.joinDate && errors.joinDate.message}
                        </small>
         </div>
         <hr/>
         <button type="submit">Submit</button>
        </form>

     );
 }

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<EmployeeForm/>);
